mapper.update:
UPDATE TBL_BOARD2
   SET BRDTITLE=#{brdTitle}
     , BRDWRITER=#{brdWriter}
     , BRDMEMO=#{brdMemo}
 WHERE BRDNO=#{brdNo}
   AND BRDDELETEFLAG = 'N'

UPDATE TBL_BOARD2
   SET BRDHIT = BRDHIT + 1
 WHERE BRDNO = #{brdNo}

mapper.delete:
UPDATE TBL_BOARD2 
   SET BRDDELETEFLAG = 'Y'
 WHERE BRDNO=#{brdNo}
