mapper.update:
UPDATE TBL_BOARD2
   SET BRDTITLE=#{brdTitle}
     , BRDWRITER=#{brdWriter}
     , BRDMEMO=#{brdMemo}
 WHERE BRDNO=#{brdNo}
   AND BRDDELETEFLAG = 'N'

UPDATE TBL_BOARD2
   SET BRDHIT = BRDHIT + 1
 WHERE BRDNO = #{brdNo}

mapper.delete:
UPDATE TBL_BOARD2 
   SET BRDDELETEFLAG = 'Y'
 WHERE BRDNO=#{brdNo}

DELETE
  FROM TBL_BOARDFILE
 WHERE FILENO IN(

 						${item}

 				)

UPDATE TBL_BOARDREPLY
   SET REDELETEFLAG = 'Y'
 WHERE RENO = #{reNo}

mapper.update:
UPDATE TBL_BOARDREPLY
   SET REMEMO =#{reMemo}
 WHERE RENO = #{reNo}
